<?php/*Plugin Name: Mayuko footer Tag ManagerPlugin URI: http://mayuko.meDescription: This is a plugin insert anything you like into footer area.Author: Mayuko MoriyamaVersion: 0.1Author URI: http://mayuko.me*/function NC_tag_manager() {	if(get_option('mayuko_tag')){		echo esc_html( get_option('mayuko_tag') );	}}add_action('wp_footer', 'NC_tag_manager', 10000);/* -- generate Admin field -- */function NC_setting_callback_function() { ?> 	<textarea name="mayuko_tag" id="mayuko_tag" type="text" rows="5" style="width:100%"><?php 		echo esc_html( get_option('mayuko_tag') ); ?></textarea>	<?php}function NC_settings_api_init() { 	add_settings_field('mayuko_tag',		'insert something on footer area',		'NC_setting_callback_function',		'reading',		'default'); 	 	register_setting('reading','mayuko_tag'); } add_action('admin_init', 'NC_settings_api_init');?>